import React from 'react';
import { Link } from 'react-router-dom';
import { Button, Input, message, Icon } from 'flwww';
import { registerBusinessAsync } from '../../redux/RegisterBusiness/register-business.actions';
import CustomInput from '../../components/CustomInput/custom-input.component';

import './create-business.styles.css';
import Tags from '../../components/Tags/tags.component';
import TagsItems from '../../utils/tags';
import { connect } from 'react-redux';

class CreateBusiness extends React.Component {
  constructor(){
    super();
    this.state = {
      businessname: '',
      phoneno: '',
      rcNumber: '',
      businessowner: '',
      email: '',
      address: '',
      city: '',
      state: '',
      tag: [],
      category: '',
      lat: '',
      lng: '',
      registered: '',
      showTag: false,
      tagItems:'',
      loading: false
    }
  }

  componentDidMount() {

  }

  onInputChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    this.setState({ [name]: value })
  }

  openTag = () => {
    this.setState({ showTag: true })
  }
  closeTag = () => {
    this.setState({ showTag:  false })
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const { registerBusinessAsync } = this.props;
    const {
      businessname, phoneno, rcNumber,
      businessowner, email, address,
      city, state, tag, category,
      lat, lng, registered
    } = this.state;
    registerBusinessAsync(
      businessname, phoneno, rcNumber,
      businessowner, email, address,
      city, state, tag, category,
      lat, lng, registered
    );
  }

  render() {
    const filteredTags = TagsItems.filter(item => {
      return item.toLowerCase().includes(this.state.tagItems.toLowerCase())
    });
    return (
      <div className='create-business_container'>
        <div className='create-business_link'>
          <Link to='/dashboard'>
            <Icon type="arrowLeftCircle" size="30px" color="#079992"/>
            <p>Back</p>
          </Link>
        </div>
        <form onSubmit={this.handleSubmit} 
          className='create-business_form'>

          <Input 
            className='custom-input' required 
            type='text' 
            autoComplete='off'
            placeholder='Business Name *' 
            name='businessname' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='number' 
            autoComplete='off'
            placeholder='Phone Number *' 
            name='phoneno' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            autoComplete='off'
            placeholder='RC Number *' 
            name='rcNumber' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            autoComplete='off'
            placeholder='Address *' 
            name='address' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            autoComplete='off'
            placeholder='City *' 
            name='city' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            autoComplete='off'
            placeholder='State *' 
            name='state' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            autoComplete='off'
            placeholder='category *' 
            name='category' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            autoComplete='off'
            placeholder='tag *' 
            name='tagItems' 
            onChange={this.onInputChange}
            onClick={this.openTag}/>
            {
              this.state.showTag ? <Tags filteredTags={filteredTags}/> : null
            }

          <Input 
            className='custom-input' required 
            type='text' 
            autoComplete='off'
            placeholder='Business Owner *' 
            name='businessowner' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' 
            type='email' 
            autoComplete='off'
            placeholder='Email (optional)' 
            name='email' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <div>
            <CustomInput
              style={{ width: '0.8rem' }}
              type='checkbox' 
              placeholder='registered' 
              name='registered' 
              onChange={this.onInputChange}/>
              <span>
                Registered with CAC
              </span>
          </div>
          <Button 
            className='custom-button' 
            type='submit' 
            outlined
            loading={this.state.loading}
            >Submit Details</Button>
        </form>
      </div>
    );
  }
}

// const mapStateToProps = (state) => ({

// })

const mapDispatchToProps = (dispatch) => ({
  registerBusinessAsync: (
    businessowner, businessname,
    phoneno, email, category,
    latitude, longitude, registered, rcNumber,
    city, state, address, tag
  ) => dispatch(registerBusinessAsync(
    businessowner, businessname,
    phoneno, email, category,
    latitude, longitude, registered, rcNumber,
    city, state, address, tag
  ))
});

export default connect(null, mapDispatchToProps)(CreateBusiness);
