import React from 'react';
import CustomInput from '../../components/CustomInput/custom-input.component';
import CustomButton from '../../components/CustomButton/custom-button.component';

import './create-business.styles.css';

class CreateBusiness extends React.Component {
  constructor(){
    super();
    this.state = {
      businessname: '',
      phoneno: '',
      rcNumber: '',
      businessowner: '',
      email: '',
      address: '',
      city: '',
      state: '',
      tag: [],
      category: '',
      lat: '',
      lng: '',
      registered: ''
    }
  }

  componentDidMount() {

  }

  onInputChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    console.log(value)
    this.setState({ [name]: value })
  }

  addTag = (value) => {
    this.setState({ tag: this.state.tag.push(value)} )
  }

  handleSubmit = (event) => {
    event.preventDefault();
    console.log(this.state);
  }

  render() {
    return (
      <div className='create-business_container'>
        <form onSubmit={this.handleSubmit} className='create-business_form'>

          <CustomInput className='custom-input' required type='text' placeholder='Business Name *' name='businessname' onChange={this.onInputChange}/>
          <CustomInput className='custom-input' required type='text' placeholder='Phone Number *' name='phoneno' onChange={this.onInputChange}/>
          <CustomInput className='custom-input' required type='text' placeholder='RC Number *' name='rcNumber' onChange={this.onInputChange}/>

          <CustomInput className='custom-input' required type='text' placeholder='Address *' name='address' onChange={this.onInputChange}/>
          <CustomInput className='custom-input' required type='text' placeholder='City *' name='city' onChange={this.onInputChange}/>
          <CustomInput className='custom-input' required type='text' placeholder='State *' name='state' onChange={this.onInputChange}/>

          <CustomInput className='custom-input' required type='text' placeholder='category *' name='category' onChange={this.onInputChange}/>
          <CustomInput className='custom-input' required type='text' placeholder='tag *' name='tag' onChange={(event) => this.addTag(event.target.value)}/>


          <CustomInput className='custom-input' required type='text' placeholder='Business Owner *' name='businessowner' onChange={this.onInputChange}/>
          <CustomInput className='custom-input' type='email' placeholder='Email (optional)' name='email' onChange={this.onInputChange}/>

          <CustomInput type='checkbox' placeholder='registered' name='registered' onChange={this.onInputChange}/>
          <CustomButton className='custom-button' type='submit'>Submit Details</CustomButton>
        </form>
      </div>
    );
  }
}

export default CreateBusiness;
