import React from "react";
import { Link } from 'react-router-dom'
import { Input, Button, Icon } from "flwww";

import './admin-login.styles.css';

class AdminLogin extends React.Component {
  constructor() {
    super();
    this.state = {
      name: null,
      password: null,
      error: false,
      loading: false
    }
  }

  handleChange = (event) => {
    const { password } = this.state;

    if (!this.state.name || !password) {
      this.setState({ error: true})
      return;
    }
    const { name, value } = event.target;
    this.setState({ [name]: value })
  }

  render() {
    return (
      <div className='admin-login'>
        <div>
          <Link to='/' className='admin-login_link'>
            <Icon type="arrowLeftCircle" size="30px" color="#079992"/>

            <p>Back</p>
          </Link>
        </div>
        <Input
          style={{ fontSize: "1rem", marginBottom: ".7rem" }}
          icon="user"
          error={this.state.error}
          placeholder="username" 
          onChange={this.handleChange}/>

        <Input
          style={{ fontSize: "1rem", marginBottom: ".7rem" }}
          placeholder="Password" 
          onChange={this.handleChange}/>

        <Button outlined loading={this.state.loading} onClick={this.handleError}>Login</Button>
      </div>
    );
  }
}

export default AdminLogin;
