import React from "react";
import { Link } from 'react-router-dom'
import { Input, Button, Icon } from "flwww";

import './admin-login.styles.css';
import { connect } from "react-redux";

class AdminLogin extends React.Component {
  constructor() {
    super();
    this.state = {
      name: null,
      password: null,
      error: {
        name: false,
        password: false
      },
      loading: false
    }
  }

  handleSubmit = () => {
    const { name, password } = this.state;

    if (!name) {
      this.setState({ error: { name: true }})
      return;
    }
    if (!password) {
      this.setState({ error: { password: true }})
      return;
    }
    this.setState({error: {
      name: false,
      password: false
    }})
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value })
  }

  render() {
    return (
      <div className='admin-login'>
        <div>
          <Link to='/' className='admin-login_link'>
            <Icon type="arrowLeftCircle" size="30px" color="#079992"/>

            <p>Back</p>
          </Link>
        </div>
        <Input
          style={{ fontSize: "1rem", marginBottom: ".7rem" }}
          icon="user"
          name='name'
          error={this.state.error.name}
          placeholder="username" 
          onChange={this.handleChange}/>

        <Input
          style={{ fontSize: "1rem", marginBottom: ".7rem" }}
          error={this.state.error.password}
          name='password'
          placeholder="Password" 
          onChange={this.handleChange}/>

        <Button outlined loading={this.state.loading} onClick={this.handleSubmit}>Login</Button>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  loginUserAsync: (name, password) => dispatch(loginUserAsync(name, password))
});

export default connect(null, mapDispatchToProps)(AdminLogin);
