import React from 'react';
import CustomInput from '../../components/CustomInput/custom-input.component';
import CustomButton from '../../components/CustomButton/custom-button.component';

import './create-business.styles.css';

class CreateBusiness extends React.Component {
  constructor(){
    super();
    this.state = {
      businessname: '',
      phoneno: '',
      registered: '',
      rcNumber: ''
    }
  }

  componentDidMount() {

  }

  onInputChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    console.log(value)
    this.setState({ [name]: value })
  }

  handleSubmit = (event) => {
    event.preventDefault();
    console.log(this.state);
  }

  render() {
    return (
      <div className='create-business_container'>
        <form onSubmit={this.handleSubmit} className='create-business_form'>

          <CustomInput className='custom-input' required type='text' placeholder='Business Name*' name='businessname' onChange={this.onInputChange}/>
          <CustomInput className='custom-input' required type='text' placeholder='Phone Number*' name='phoneno' onChange={this.onInputChange}/>
          <CustomInput className='custom-input' required type='text' placeholder='RC Number' name='rcNumber' onChange={this.onInputChange}/>

          <CustomInput className='custom-input' type='text' placeholder='Business Owner*' name='businessowner' onChange={this.onInputChange}/>
          <CustomInput className='custom-input' type='email' placeholder='Email (optional)' name='rcNumber' onChange={this.onInputChange}/>

          <CustomInput type='checkbox' placeholder='registered' name='registered' onChange={this.onInputChange}/>
          <CustomButton className='custom-button' type='submit'>Submit Details</CustomButton>
        </form>
      </div>
    );
  }
}

export default CreateBusiness;
