import React from 'react';
import CustomInput from '../../components/CustomInput/custom-input.component';
import CustomButton from '../../components/CustomButton/custom-button.component';

import { Button, Input } from 'flwww';

import './create-business.styles.css';
import Tags from '../../components/Tags/tags.component';
import TagsItems from '../../utils/tags';

class CreateBusiness extends React.Component {
  constructor(){
    super();
    this.state = {
      businessname: '',
      phoneno: '',
      rcNumber: '',
      businessowner: '',
      email: '',
      address: '',
      city: '',
      state: '',
      tag: [],
      category: '',
      lat: '',
      lng: '',
      registered: '',
      showTag: false,
      tagItems:''
    }
  }

  componentDidMount() {

  }

  onInputChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    this.setState({ [name]: value })
  }

  openTag = () => {
    this.setState({ showTag: true })
  }
  closeTag = () => {
    this.setState({ showTag:  false })
  }

  handleSubmit = (event) => {
    event.preventDefault();
    // const {
    //   businessname, phoneno, rcNumber,
    //   businessowner, email, address,
    //   city, state, tag, category,
    //   lat, lng, registered
    // } = this.state;
  }

  render() {
    const filteredTags = TagsItems.filter(item => {
      return item.toLowerCase().includes(this.state.tagItems.toLowerCase())
    });
    return (
      <div className='create-business_container'>
        <form onSubmit={this.handleSubmit} 
          className='create-business_form'>

          <Input 
            className='custom-input' required 
            type='text' 
            placeholder='Business Name *' 
            name='businessname' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            placeholder='Phone Number *' 
            name='phoneno' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            placeholder='RC Number *' 
            name='rcNumber' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            placeholder='Address *' 
            name='address' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            placeholder='City *' 
            name='city' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            placeholder='State *' 
            name='state' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            placeholder='category *' 
            name='category' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' required 
            type='text' 
            placeholder='tag *' 
            name='tagItems' 
            onChange={this.onInputChange}
            onClick={this.openTag}/>
            {
              this.state.showTag ? <Tags filteredTags={filteredTags}/> : null
            }

          <Input 
            className='custom-input' required 
            type='text' 
            placeholder='Business Owner *' 
            name='businessowner' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <Input 
            className='custom-input' 
            type='email' 
            placeholder='Email (optional)' 
            name='email' 
            onChange={this.onInputChange}
            onClick={this.closeTag}/>

          <div>
            <Input 
              type='checkbox' 
              placeholder='registered' 
              name='registered' 
              onChange={this.onInputChange}/>
              <span>
                Registered with CAC
              </span>
          </div>
          <Button 
            className='custom-button' 
            type='submit' round>Submit Details</Button>
        </form>
      </div>
    );
  }
}

export default CreateBusiness;
